function displayBodyLang(country,enBody,bodyPt){"Brazil"==country?bodyPt.style.display="block":enBody.style.display="block"}export function getLocation(){const bodyPt=document.querySelector("#pt-body"),enBody=document.querySelector("#en-body");enBody.style.display="none",bodyPt.style.display="none";try{if(!navigator.geolocation)return void console.error("Geolocation is not supported by this browser.");navigator.geolocation.getCurrentPosition(async position=>{const{latitude:latitude,longitude:longitude}=position.coords,apiKey="b6219e522f57eb2c8547d613a9b0224a",countryFindUrl=`http://api.positionstack.com/v1/reverse?access_key=${apiKey}&query=${latitude},${longitude}`,response=await fetch(countryFindUrl);if(200!=response.status)return;const data=await response.json(),{country:country}=data.data[0];displayBodyLang(country,enBody,bodyPt)})}catch(e){return console.error(e),void(enBody.style.display="block")}}